import React, { Component } from "react";
import ThumbnailImage from "../ThumbnailImage/ThumbnailImage";
import "./CommentContainer.scss";

class CommentContainer extends Component {
  render() {
    const { userImage, username, rating, comment, numberOfLikes } = this.props;
    return (
      <div className="CommentContainer">
        <div className="commentWrapper">
          <header className="commentHeader">
            <div className="profileBlock">
              <ThumbnailImage src={userImage} size="32px" />
              <div className="commentUser">{username}</div>
            </div>
            {rating && (
              <div className="userActionStatus">
                <img
                  alt="star"
                  width="16px"
                  height="16px"
                  src="data:image/svg+xml;base64,"
                  className="starImage"
                />
                <span>{rating}</span>
              </div>
            )}
          </header>
          <div className="commentBlock">
            <div className="commentText">{comment}</div>
          </div>
          <div className="likeReplyBlock">
            <img
              alt="likeReply"
              src="data:image/svg+xml;base64,"
              width="18px"
              height="18px"
              className="likeImage"
            />
            <em>{numberOfLikes}</em>
          </div>
          <div className="userActionBlock">
            <button className="userActionButton">좋아요</button>
          </div>
        </div>
      </div>
    );
  }
}

export default CommentContainer;
