import React, { Component } from "react";
import ThumbnailImage from "../ThumbnailImage/ThumbnailImage";
import "./ShowCommentBlock.scss";

class ShowCommentBlock extends Component {
  render() {
    const { comment, user_image, openModal } = this.props;
    return (
      <section className="ShowCommentBlock">
        <div className="showComment">
          <ThumbnailImage src={user_image} />
          <div className="selfCommentText">{comment}</div>
          <ul className="commentUpdateButtons">
            <li className="commentUpdateButtonsList">
              <button
                className="commentUpdateButtonsListItem"
                onClick={() => openModal("alert")}
              >
                <img
                  alt="delete comment"
                  src="data:image/svg+xml;base64,"
                />
                삭제
              </button>
            </li>
            <li className="commentUpdateButtonsList">
              <button
                className="commentUpdateButtonsListItem"
                onClick={() => openModal("comment")}
              >
                <img
                  alt="edit comment"
                  src="data:image/svg+xml;base64,"
                />
                수정
              </button>
            </li>
          </ul>
        </div>
      </section>
    );
  }
}

export default ShowCommentBlock;
